// KanbanBoard.js

import React, { useState, useEffect } from 'react';
import TicketCard from './TicketCard';
import './KanbanBoard.css'; // Assuming you have a CSS file for KanbanBoard

const KanbanBoard = ({ tickets, groupOption, sortOption }) => {
  const [groupedTickets, setGroupedTickets] = useState([]);

  useEffect(() => {
    // Logic to group and sort tickets based on groupOption and sortOption
    // Update the 'groupedTickets' state
  }, [tickets, groupOption, sortOption]);

  return (
    <div className="kanban-board">
      {/* Add headings for each priority level */}
      <div className="priority-column">
        <h2>Urgent</h2>
        {/* Render tickets with priority level 4 */}
        {groupedTickets
          .filter((ticket) => ticket.priority === 4)
          .map((ticket) => (
            <TicketCard key={ticket.id} ticket={ticket} />
          ))}
      </div>
      <div className="priority-column">
        <h2>High</h2>
        {/* Render tickets with priority level 3 */}
        {groupedTickets
          .filter((ticket) => ticket.priority === 3)
          .map((ticket) => (
            <TicketCard key={ticket.id} ticket={ticket} />
          ))}
      </div>
      <div className="priority-column">
        <h2>Medium</h2>
        {/* Render tickets with priority level 2 */}
        {groupedTickets
          .filter((ticket) => ticket.priority === 2)
          .map((ticket) => (
            <TicketCard key={ticket.id} ticket={ticket} />
          ))}
      </div>
      <div className="priority-column">
        <h2>Low</h2>
        {/* Render tickets with priority level 1 */}
        {groupedTickets
          .filter((ticket) => ticket.priority === 1)
          .map((ticket) => (
            <TicketCard key={ticket.id} ticket={ticket} />
          ))}
      </div>
      <div className="priority-column">
        <h2>No Priority</h2>
        {/* Render tickets with priority level 0 */}
        {groupedTickets
          .filter((ticket) => ticket.priority === 0)
          .map((ticket) => (
            <TicketCard key={ticket.id} ticket={ticket} />
          ))}
      </div>
    </div>
  );
};

export default KanbanBoard;
